import React, { useState } from 'react';
import PrivateNavbar from '../components/PrivateNavbar'; // Import your PrivateNavbar component
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState(null);
  const [inputPreview, setInputPreview] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setInputPreview(URL.createObjectURL(file)); // Show uploaded image
    setResult(null); // Reset result on new upload
  };

  const handleAnalyze = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile);
    setLoading(true);

    try {
      const response = await fetch('http://localhost:3000/predict', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setResult(data);  // { anomaly_detected, type, confidence, severity }
    } catch (error) {
      console.error('Prediction error:', error);
      setResult({ anomaly_detected: false, type: 'Error', confidence: 0, severity: 'None' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-page">
      {/* Include the PrivateNavbar here */}
      <PrivateNavbar />

      <h1 className="dashboard-title">Anomaly Detection Dashboard</h1>

      <div className="dashboard-main">
        {/* Upload Section */}
        <div className="upload-section">
          <p>Upload an image to detect defects</p>
          <input type="file" accept="image/*" className="file-input" onChange={handleFileChange} />
          <button className="analyze-button" onClick={handleAnalyze} disabled={loading}>
            {loading ? 'Analyzing...' : 'Analyze'}
          </button>
        </div>

        {/* Output Panel */}
        <div className="output-panel">
          <h2>Model Output</h2>
          {result ? (
            <>
              <p className="output-status">
                {result.anomaly_detected ? '✅ Anomaly Detected' : '✔️ No Defect'}
              </p>
              <p>Type: <span className="highlight">{result.type}</span></p>
              <p>Confidence: <span className="confidence">{(result.confidence * 100).toFixed(1)}%</span></p>
              <p>
                Severity:{' '}
                <span className={`severity-${(result.severity || '').toLowerCase()}`}>
                  {result.severity}
                </span>
              </p>
            </>
          ) : (
            <p>No prediction yet.</p>
          )}
        </div>
      </div>

      {/* Image Previews */}
      <div className="preview-section">
        <div className="image-card">
          <p className="image-label">Input Image</p>
          {inputPreview ? (
            <img src={inputPreview} alt="Input Preview" className="image-placeholder" />
          ) : (
            <div className="image-placeholder"></div>
          )}
        </div>
        <div className="image-card">
          <p className="image-label">Prediction Overlay</p>
          <div className="image-placeholder"></div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
