import React from 'react';
import '../styles/Dashboard.css';

const Dashboard = () => {
  return (
    <div className="dashboard-page">
      <h1 className="dashboard-title">Anomaly Detection Dashboard</h1>

      <div className="dashboard-main">
        {/* Upload Section */}
        <div className="upload-section">
          <p>Upload an image to detect defects</p>
          <input type="file" accept="image/*" className="file-input" />
          <button className="analyze-button">Analyze</button>
        </div>

        {/* Output Panel */}
        <div className="output-panel">
          <h2>Model Output</h2>
          <p className="output-status">✅ Anomaly Detected</p>
          <p>Type: <span className="highlight">Crack</span></p>
          <p>Confidence: <span className="confidence">96.7%</span></p>
          <p>Severity: <span className="severity-high">High</span></p>
        </div>
      </div>

      {/* Image Previews */}
      <div className="preview-section">
        <div className="image-card">
          <p className="image-label">Input Image</p>
          <div className="image-placeholder"></div>
        </div>
        <div className="image-card">
          <p className="image-label">Prediction Overlay</p>
          <div className="image-placeholder"></div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;


