import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Reports.css';

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [email, setEmail] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const storedEmail = localStorage.getItem('userEmail');
    if (!storedEmail) {
      navigate('/');
      return;
    }
    setEmail(storedEmail);

    const fetchReports = async () => {
      try {
        const response = await fetch('http://localhost:5000/get-reports', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ user_email: storedEmail }),
        });

        const data = await response.json();
        if (response.ok) {
          setReports(data);
        } else {
          console.error('Failed to fetch reports:', data.error);
        }
      } catch (err) {
        console.error('Error fetching reports:', err);
      }
    };

    fetchReports();
  }, [navigate]);

  return (
    <div className="reports-page">
      <h1 className="reports-title">Anomaly Reports</h1>

      <div className="reports-table">
        <div className="table-header">
          <span>ID</span>
          <span>Type</span>
          <span>Confidence</span>
          <span>Severity</span>
          <span>Timestamp</span>
          <span>Report</span>
        </div>

        {reports.map((r, index) => (
          <div key={r._id || index} className="table-row">
            <span>{index + 1}</span>
            <span>{r.type}</span>
            <span>{(parseFloat(r.confidence) * 100).toFixed(1)}%</span>
            <span className={`severity ${r.severity.toLowerCase()}`}>{r.severity}</span>
            <span>{new Date(r.timestamp).toLocaleString()}</span>
            <span>
              <button className="download-btn">Download</button>
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reports;
