import React, { useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setPrediction(null);
    setError(null);
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedFile) {
      setError('Please select an image file first.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Prepare form data for the POST request
      const formData = new FormData();
      formData.append('file', selectedFile);

      // Send POST request to Flask backend
      const response = await axios.post('http://localhost:3000/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setPrediction(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Error making prediction.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto', fontFamily: 'Arial, sans-serif' }}>
      <h2>Image Anomaly Detection</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit" disabled={loading} style={{ marginLeft: '10px' }}>
          {loading ? 'Predicting...' : 'Predict'}
        </button>
      </form>

      {error && (
        <div style={{ color: 'red', marginTop: '15px' }}>
          <strong>Error:</strong> {error}
        </div>
      )}

      {prediction && (
        <div style={{ marginTop: '20px' }}>
          <h3>Prediction Results</h3>
          <p>
            <strong>Type:</strong> {prediction.type}
          </p>
          <p>
            <strong>Confidence:</strong> {(prediction.confidence * 100).toFixed(2)}%
          </p>
          <p>
            <strong>Severity:</strong> {prediction.severity}
          </p>
          <p>
            <strong>Anomaly Detected:</strong> {prediction.anomaly_detected ? 'Yes' : 'No'}
          </p>

          {prediction.overlay && (
            <div style={{ marginTop: '20px' }}>
              <img
                src={prediction.overlay}
                alt="Prediction Overlay"
                style={{ maxWidth: '100%', border: '1px solid #ccc', borderRadius: '8px' }}
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
