import React, { useState, useEffect } from 'react';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState(null);
  const [inputPreview, setInputPreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [email, setEmail] = useState('');

  useEffect(() => {
    const storedEmail = localStorage.getItem('userEmail');
    if (storedEmail) {
      setEmail(storedEmail);
    } else {
      alert('No user email found. Please log in again.');
    }
  }, []);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setInputPreview(URL.createObjectURL(file));
    setResult(null);
  };

  const handleAnalyze = async () => {
    if (!selectedFile) {
      alert('Please select a file before analyzing.');
      return;
    }

    if (!email) {
      alert('User email missing. Please log in again.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('user_email', email);

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (!response.ok) {
        alert(data.error || 'Prediction failed.');
        setResult(null);
      } else {
        setResult(data);
      }
    } catch (error) {
      console.error('Prediction error:', error);
      alert('Prediction error occurred.');
      setResult(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Industrial Screw Defect Detection</h1>

      <div className="upload-section">
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
          className="file-input"
        />
        <button onClick={handleAnalyze} disabled={loading || !selectedFile}>
          {loading ? 'Analyzing...' : 'Analyze'}
        </button>
      </div>

      {inputPreview && (
        <div className="image-preview-section">
          <div className="image-card">
            <p>Input Image</p>
            <img src={inputPreview} alt="Input" className="image-preview" />
          </div>

          <div className="image-card">
            <p>Prediction Overlay</p>
            {result && result.heatmap_image ? (
              <img
                src={result.heatmap_image}
                alt="Heatmap Overlay"
                className="image-preview"
              />
            ) : (
              <div className="image-placeholder" />
            )}
          </div>
        </div>
      )}

      {result && (
        <div className="result-section">
          <h2>Result</h2>
          <p>
            <strong>Status:</strong> {result.type}
          </p>
          <p>
            <strong>Confidence:</strong> {(result.confidence * 100).toFixed(2)}%
          </p>
          <p>
            <strong>Severity:</strong> {result.severity}
          </p>
          <p>
            <strong>Timestamp:</strong>{' '}
            {new Date(result.timestamp).toLocaleString()}
          </p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
