import React, { useEffect, useState } from 'react';
import '../styles/Reports.css';

const Reports = () => {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    const fetchReports = async () => {
      const email = localStorage.getItem('userEmail');
      if (!email) {
        alert('No user email found. Please log in again.');
        window.location.href = '/';
        return;
      }

      try {
        const response = await fetch(`http://localhost:5000/reports?username=${email}`);
        const data = await response.json();

        if (response.ok) {
          setReports(data);
        } else {
          console.error('Failed to fetch reports:', data.error);
        }
      } catch (err) {
        console.error('Error fetching reports:', err);
      }
    };

    fetchReports();
  }, []);

  return (
    <div className="reports-page">
      <h1 className="reports-title">Anomaly Reports</h1>

      <div className="reports-table">
        <div className="table-header">
          <span>ID</span>
          <span>Type</span>
          <span>Confidence</span>
          <span>Severity</span>
          <span>Timestamp</span>
          <span>Report</span>
        </div>

        {reports.map((r, index) => (
          <div key={r._id || index} className="table-row">
            <span>{index + 1}</span>
            <span>{r.result?.type || '-'}</span>
            <span>{(parseFloat(r.result?.confidence || 0) * 100).toFixed(1)}%</span>
            <span className={`severity ${r.result?.severity?.toLowerCase()}`}>{r.result?.severity || '-'}</span>
            <span>{new Date(r.timestamp).toLocaleString()}</span>
            <span>
              <button className="download-btn">Download</button>
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Reports;
